//  String class test program
//
//  Name:  Donald Harmon
//  Tests: assignment, uses == and constructors
//

#include "string.hpp"
#include <cassert>
#include <iostream>

//===========================================================================
int main (){

	{
		// SETUP FIXTURE
		String str;
		String result;

		// EMPTY TEST
		result = str;

		// VERIFY
		assert(str == '\0');
		assert(result == '\0');
		assert(result.length() == 0);
		assert(result.capacity() == 0);
	}

	{
		// SETUP FIXTURE
		String str(' ');
		String result;

		// SPACE TEST
		result = str;

		// VERIFY
		assert(str == ' ');
		assert(result == ' ');
		assert(result.length() == 1);
		assert(result.capacity() == 1);
	}

	{
		// SETUP FIXTURE
		String str('\n');
		String result;

		// ONE CHAR TEST
		result = str;

		// VERIFY
		assert(str == '\n');
		assert(result == '\n');
		assert(result.length() == 1);
		assert(result.capacity() == 1);
	}

	{
		// SETUP FIXTURE
		String str("I'm going to assign myself to the string result, and help to test the assignment operator overload! Yay!");
		String result;

		// MID TEST
		result = str;

		// VERIFY
		assert(str == "I'm going to assign myself to the string result, and help to test the assignment operator overload! Yay!");
		assert(result == "I'm going to assign myself to the string result, and help to test the assignment operator overload! Yay!");
		assert(result.length() == 104);
		assert(result.capacity() == 104);
	}

	{
		// SETUP FIXTURE
		String str("");
		String result;

		// LONG TEST
		result = str;

		// VERIFY
		assert(str == "");
		assert(result == "");
		assert(result.length() == 2000);
		assert(result.capacity() == 2000);
	}


    std::cout << "Done testing assignment operator." << std::endl;

}