//
//  Name:  Donald Harmon
//  Tests: swap, uses == and constructors
//

#include "string.hpp"
#include <cassert>
#include <iostream>

//===========================================================================
int main() {

	{
		// SETUP FIXTURE
		String str("I AM NOT AN EMPTY STRING");
		String i_am_though;

		// TEST
		str.swap(i_am_though);

		// VERIFY
		assert(str == '\0');
		assert(i_am_though == "I AM NOT AN EMPTY STRING");
		assert(str.length() == 0);
		assert(i_am_though.length() == 24);
		assert(str.length() == str.capacity());
		assert(i_am_though.length() == i_am_though.capacity());

		// TEST (SWAP BACK)
		str.swap(i_am_though);

		// VERIFY
		assert(i_am_though == '\0');
		assert(str == "I AM NOT AN EMPTY STRING");
		assert(str.length() == 24);
		assert(i_am_though.length() == 0);
		assert(str.length() == str.capacity());
		assert(i_am_though.length() == i_am_though.capacity());
	}

	{
		// SETUP FIXTURE
		String str('&');
		String str_two('\t');

		// TEST
		str.swap(str_two);

		// VERIFY
		assert(str == "\t");
		assert(str_two == '&');
		assert(str.length() == 1);
		assert(str.capacity() == 1);
		assert(str_two.length() == 1);
		assert(str_two.capacity() == 1);
		assert(str.length() == str.capacity());

		// TEST (SWAP BACK)
		str.swap(str_two);

		// VERIFY
		assert(str_two == "\t");
		assert(str == '&');
		assert(str.length() == 1);
		assert(str.capacity() == 1);
		assert(str_two.length() == 1);
		assert(str_two.capacity() == 1);
		assert(str.length() == str.capacity());
	}

	{
		// SETUP FIXTURE
		String str;
		String str_two("Hello World!");

		// TEST
		str.swap(str_two);

		// VERIFY
		assert(str == "Hello World!");
		assert(str_two == '\0');
		assert(str.length() == 12);
		assert(str.capacity() == 12);
		assert(str_two.length() == 0);
		assert(str_two.capacity() == 0);
		assert(str.length() == str.capacity());

		// TEST (SWAP BACK)
		str.swap(str_two);

		// VERIFY
		assert(str_two == "Hello World!");
		assert(str == '\0');
		assert(str.length() == 0);
		assert(str.capacity() == 0);
		assert(str_two.length() == 12);
		assert(str_two.capacity() == 12);
		assert(str.length() == str.capacity());

	}

	{
		// SETUP FIXTURE
		String str("\t \r \n ");
		String str_two("\r \t \n");

		// TEST
		str.swap(str_two);

		// VERIFY
		assert(str == "\r \t \n");
		assert(str_two == "\t \r \n ");
		assert(str.length() == 5);
		assert(str_two.length() == 6);
		assert(str.capacity() == 5);
		assert(str_two.capacity() == 6);
		assert(str.length() == str.capacity());

		// TEST (SWAP BACK)
		str.swap(str_two);

		// VERIFY
		assert(str_two == "\r \t \n");
		assert(str == "\t \r \n ");
		assert(str.length() == 6);
		assert(str_two.length() == 5);
		assert(str.capacity() == 6);
		assert(str_two.capacity() == 5);
		assert(str.length() == str.capacity());

	}

	{
		// SETUP FIXTURE
		String str('A');
		String str_two("Hello World!, I'm just glad to help with testing a mighty fine swap function!");

		// TEST
		str.swap(str_two);

		// VERIFY
		assert(str == "Hello World!, I'm just glad to help with testing a mighty fine swap function!");
		assert(str_two == 'A');
		assert(str.length() == 77);
		assert(str_two.length() == 1);
		assert(str.capacity() == 77);
		assert(str_two.capacity() == 1);
		assert(str.length() == str.capacity());

		// TEST (SWAP BACK)
		str.swap(str_two);

		// VERIFY
		assert(str_two == "Hello World!, I'm just glad to help with testing a mighty fine swap function!");
		assert(str == 'A');
		assert(str.length() == 1);
		assert(str_two.length() == 77);
		assert(str.capacity() == 1);
		assert(str_two.capacity() == 77);
		assert(str.length() == str.capacity());
		assert(str_two.length() == str_two.capacity());

	}

	{
		// SETUP FIXTURE
		String str("");
		String str_two("BEG  END");

		// TEST
		str.swap(str_two);

		// VERIFY
		assert(str_two == "");
		assert(str == "BEG  END");
		assert(str.length() == 1500);
		assert(str.capacity() == 1500);
		assert(str_two.length() == 1500);
		assert(str_two.capacity() == 1500);
		assert(str.length() == str.capacity());

		// TEST (SWAP BACK)
		str.swap(str_two);

		// VERIFY
		assert(str == "");
		assert(str_two == "BEG  END");
		assert(str.length() == 1500);
		assert(str.capacity() == 1500);
		assert(str_two.length() == 1500);
		assert(str_two.capacity() == 1500);
		assert(str.length() == str.capacity());

	}

	{
		// SETUP FIXTURE
		String str("");
		String str_two("I'm small!!!\t");

		// TEST
		str.swap(str_two);

		// VERIFY
		assert(str == "I'm small!!!\t");
		assert(str_two == "");
		assert(str.length() == 13);
		assert(str_two.length() == 1500);
		assert(str.length() == str.capacity());
		assert(str_two.length() == str_two.capacity());

		// TEST (SWAP BACK)
		str.swap(str_two);

		// VERIFY 
		assert(str_two == "I'm small!!!\t");
		assert(str == "");
		assert(str.length() == 1500);
		assert(str_two.length() == 13);
		assert(str.length() == str.capacity());
		assert(str_two.length() == str_two.capacity());
	}


	std::cout << "Done testing swap" << std::endl;


}